uint32_t sin_res_sinDate16_addr_get(void)
{
    return (uint32_t)SinData16;
}

uint32_t sin_res_sinDate16_len_get(void)
{
    return sizeof(SinData16)/2;
}

void sin_res_init(void)
{
    memset(&sin_res_cb, 0, sizeof(sin_res_cb_t));
}

void sin_res_deinit(void)
{
    sin_res_cb.sta = 0;
}

bool sin_res_play(int16_t *buf, uint32_t buf_len)
{
    if (buf_len == 0) {
        return false;
    }

    if (sin_res_cb.sta == 1) {
        sin_res_cb.sta = 0;
    }

    sin_res_cb.offset = 0;
    sin_res_cb.ibuf_ptr = buf;       //用户修改，播放不同提示音
    sin_res_cb.len = buf_len;

    memset((uint8_t*)sin_res_cb.obuf, 0, sizeof(sin_res_cb.obuf));

    if (SDDAC_NORMAL_SPR_16k != sddac_spr_get()) {
        // bsp_tone_play_output_mute(1);   //解决播放不同采样率的提示音出现杂音问题
    }

    GPIOADE &= ~BIT(2);
    sddac_spr_set(SDDAC_NORMAL_SPR_16k);

    // bsp_tone_play_output_mute(0);   //解决播放不同采样率的提示音出现杂音问题

    sddac_dma_kick_start((uint8_t*)sin_res_cb.obuf, SDDAC_BUF_SIZE);
    GPIOADE |= BIT(2);
    sin_res_cb.sta = 1;
    return true;
}

uint8_t sin_res_play_proc(SDDAC_IT_TYPEDEF done_type)
{
    if (sin_res_cb.sta == 1) {
        if (done_type == SDDAC_IT_ALL_DONE) {
            bsp_tone_play_volume_proc(&sin_res_cb.obuf[SDDAC_BUF_SIZE/2], &sin_res_cb.ibuf_ptr[sin_res_cb.offset], SDDAC_BUF_SIZE/2);
            sin_res_cb.offset += SDDAC_BUF_SIZE/2;
        }

        if (done_type == SDDAC_IT_HALF_DONE) {
            bsp_tone_play_volume_proc(&sin_res_cb.obuf[0], &sin_res_cb.ibuf_ptr[sin_res_cb.offset], SDDAC_BUF_SIZE/2);
            sin_res_cb.offset += SDDAC_BUF_SIZE/2;
        }

        if (sin_res_cb.offset >= sin_res_cb.len) {
            sin_res_cb.sta = 0;
            return 1;
        }
    }

    return 0;
}

